mapper.select:
SELECT id,name,group_desc AS description,parent_id AS parentId
FROM t_group
WHERE id=#{id}

SELECT id,name,group_desc AS description,parent_id AS parentId
FROM t_group
WHERE parent_id=#{parentId}

SELECT id,name,group_desc AS description,parent_id AS parentId
FROM t_group
WHERE parent_id=#{parentId}

mapper.update:
INSERT INTO t_group (name, group_desc,parent_id)
VALUES (name=#{name},group_desc=#{description},parent_id=#{parentId})
WHERE id=#{id}
